import "./ProfilePage.css";
import MbtiCriteria from "../components/MbtiCriteria";
import click from "../images/handclick.gif";
import { Link } from "react-router-dom";
import { useState } from "react";
import Swal from "sweetalert2";

function ProfilePage() {
  const criteriaArray = [
    {
      criteria: "energy",
      criterialabel: "에너지 방향",
      leftoption: "E",
      leftoptionlabel: "외향형",
      rightoption: "I",
      rightoptionlabel: "내향형",
    },
    {
      criteria: "info-processing",
      criterialabel: "인식 방식",
      leftoption: "S",
      leftoptionlabel: "감각형",
      rightoption: "N",
      rightoptionlabel: "직관형",
    },
    {
      criteria: "info-evaluation",
      criterialabel: "의사 결정",
      leftoption: "T",
      leftoptionlabel: "사고형",
      rightoption: "F",
      rightoptionlabel: "감정형",
    },
    {
      criteria: "lifestyle",
      criterialabel: "생활 양식",
      leftoption: "J",
      leftoptionlabel: "판단형",
      rightoption: "P",
      rightoptionlabel: "인식형",
    },
  ];

  const [myName, setMyName] = useState("");
  const [myMbti, setMyMbti] = useState(["", "", "", ""]);

  let changeMbti = (e) => {
    // console.log(arrIndex);
    // let newMbti = [...myMbti];
    // newMbti[arrIndex]
    // console.log(e.target.defaultValue);
    console.log(e);
  };

  return (
    <>
      <div className="input-name">
        <p>
          <span className="accent color">이름</span>을 입력해주세요
        </p>
        <input
          type="text"
          onChange={(e) => {
            setMyName(e.target.value);
          }}
        />
      </div>

      <div className="input-mbti">
        <p>
          <span className="accent color eng">MBTI</span>를 선택해주세요{" "}
          <img src={click} alt="click" width="50px" />
        </p>

        <div>
          {criteriaArray.map((criteria, i) => (
            <MbtiCriteria
              key={`criteria${i}`}
              criteria={criteria.criteria}
              criterialabel={criteria.criterialabel}
              leftoption={criteria.leftoption}
              leftoptionlabel={criteria.leftoptionlabel}
              rightoption={criteria.rightoption}
              rightoptionlabel={criteria.rightoptionlabel}
              arrIndex={i}
              changeMbti={changeMbti}
              // myMbti={myMbti}
              // setMyMbti={setMyMbti}
            />
          ))}
        </div>
      </div>

      {/* <script src="//cdn.jsdelivr.net/npm/sweetalert2@11"></script> */}

      <div>
        <Link
          to="/mybingo"
          className="btn profile"
          onClick={(e) => {
            if (myName == "") {
              // alert("이름을 기입해주세요!");
              // Swal.fire(''

              // })
              Swal.fire({
                title: "이름을 기입해주세요!",
                width: 350,
                padding: "1em",
                color: "#716add",
                backdrop: `
                  rgba(0,0,123,0.4)
                  url("/images/nyan-cat.gif")
                  left top
                  no-repeat
                `,
              });
              e.preventDefault();
            } else {
              localStorage.setItem("myName", myName);
            }

            // if (myMbti.includes("")) {
            //   alert("MBTI의 4가지 유형을 모두 클릭해주세요!");
            //   e.preventDefault();
            // } else {
            //   localStorage.setItem("my-mbti", myMbti);
            // }
          }}
        >
          👉 next 👉
          {/* 다음 👉 */}
        </Link>
      </div>
    </>
  );
}

export default ProfilePage;
