import "./MbtiCriteria.css";
import { useState } from "react";

function MbtiCriteria({
  criteria,
  criterialabel,
  leftoption,
  leftoptionlabel,
  rightoption,
  rightoptionlabel,
  // setMyMbti,
}) {
  // const [myMbti, setMyMbti] = useState(["", "", "", ""]);

  // const [myMbti, setMyMbti] = useState(
  //   { energy: "" },
  //   { "info-processing": "" },
  //   { "info-evaluation": "" },
  //   { lifestyle: "" }
  // );

  // let criteriaNames = [
  //   "energy",
  //   "info-processing",
  //   "info-evaluation",
  //   "lifestyle",
  // ];

  // let myMbti = [];

  // document.querySelector("button").addEventListener("click", function (event) {
  //   myMbti = criteriaNames.map(function (el) {
  //     return document.querySelector(
  //       "input[criteriaNames='" + el + "']:checked"
  //     ).value;
  //   });

  //   console.log(myMbti);
  // });

  const criteriaArray = [
    {
      criteria: "energy",
      criterialabel: "에너지 방향",
      leftoption: "E",
      leftoptionlabel: "외향형",
      rightoption: "I",
      rightoptionlabel: "내향형",
    },
    {
      criteria: "info-processing",
      criterialabel: "인식 방식",
      leftoption: "S",
      leftoptionlabel: "감각형",
      rightoption: "N",
      rightoptionlabel: "직관형",
    },
    {
      criteria: "info-evaluation",
      criterialabel: "의사 결정",
      leftoption: "T",
      leftoptionlabel: "사고형",
      rightoption: "F",
      rightoptionlabel: "감정형",
    },
    {
      criteria: "lifestyle",
      criterialabel: "생활 양식",
      leftoption: "J",
      leftoptionlabel: "판단형",
      rightoption: "P",
      rightoptionlabel: "인식형",
    },
  ];

  let handleChange = (e) => {
    // console.log(criteriaArray.indexOf({ criteria }));
    let indexof = _.findIndex(criteriaArray, function (o) {
      return o.criteria == `${criteria}`;
    });
    console.log(indexof);
    console.log(document.getElementsByClassName("criteria-name"));
  };

  // let handleChange = (e) => {
  //   console.log(i)
  // }

  return (
    <>
      <div className="criteria">
        <div className="option-name">{leftoptionlabel}</div>
        <div className="left-btn">
          <input
            id={leftoption}
            className="radio-btn"
            type="radio"
            value={leftoption}
            name={criteria}
            onClick={handleChange}
          />
          <label htmlFor={leftoption}>{leftoption}</label>
        </div>
        <div className="criteria-name-container">
          <div className="arrow">⬅</div>
          <div className="criteria-name">{criterialabel}</div>
          <div className="arrow">➡</div>
        </div>
        <div className="right-btn">
          <input
            id={rightoption}
            className="radio-btn"
            type="radio"
            value={rightoption}
            name={criteria}
            onClick={handleChange}
          />
          <label htmlFor={rightoption}>{rightoption}</label>
        </div>
        <div className="option-name">{rightoptionlabel}</div>
        {/* <h1>{criterialabel[0]}</h1> */}
        {/* <h1>{e.target.value}</h1> */}
      </div>
    </>
  );
}

export default MbtiCriteria;
