import BingoBoard from "../components/BingoBoard";
import "./ResultsPage.css";
import Accordion from "../components/Accordion";
import RankingBoard from "../components/RankingBoard";

// import mbtiimg from `../images/${realMbti}.png`;

// const myMbtiCount = localStorage.getItem("myMbtiCount")

function ResultsPage() {
  const myName = localStorage.getItem("myName");
  const myMbti = localStorage.getItem("myMbti");
  const myMbtiBoard = JSON.parse(localStorage.getItem("myMbtiBoard"));
  const myMistypes = JSON.parse(localStorage.getItem("myMistypes"));
  const mistypeBoardArray = JSON.parse(
    localStorage.getItem("mistypeBoardArray")
  );

  // console.log(myMbtiBoard);

  // let testByRankArray = [
  //   { mbti: "INFP", count: "18" },
  //   { mbti: "ISFJ", count: "16" },
  //   { mbti: "ENFP-Fi", count: "12" },
  //   { mbti: "ISFP", count: "11" },
  // ];

  // ì´ê±° ëŒ€ì‹  mistypeboardarray ì•ˆì— ìˆëŠ” id ì‚¬ìš©í•˜ëŠ” ê²Œ ë‚«ë‚˜?
  let myMistypesIds = myMistypes.map((item) => item.id);

  // // ì¤€ìš° test data (ì›ë˜ ì˜ˆìƒëŒ€ë¡œì¸ ì¼€ì´ìŠ¤)
  // const testMistypeCountArray = [12, 11, 16];

  // // ë‚´ test data (ì•Œê³ ë³´ë‹ˆ ë‹¤ë¥¸ ì¼€ì´ìŠ¤)
  // const myMbtiCount = 15;
  // const testMistypeCountArray = [17, 8, 9];

  // test data (ë‘ê°€ì§€ ì„ íƒê°œìˆ˜ê°€ ë˜‘ê°™ì€ ì¼€ì´ìŠ¤)

  let trueCount = (board) => {
    let count = 0;
    for (let i = 0; i < board.length; i++) {
      count += board[i].filter(Boolean).length;
    }
    return count;
  };

  // // trueCount mapìœ¼ë¡œ ì“°ëŠ” ë²•
  // console.log(
  //   myMbtiBoard
  //     .map((row) => row.filter(Boolean).length)
  //     .reduce((a, b) => a + b, 0)
  // );

  let myMbtiCount = trueCount(myMbtiBoard);

  let mistypeCount = mistypeBoardArray.map((item, i) => {
    return {
      id: item.mbti,
      count: trueCount(item.board),
    };
  });

  // let mistypeReason = myMistypes.map((item) => item.reason);
  // console.log(mistypeReason);

  let mbtiRanking = [
    ...mistypeCount,
    {
      id: myMbti,
      count: trueCount(myMbtiBoard),
    },
  ].sort((a, b) => {
    return b.count - a.count;
  });

  // 1ë“±í•œ ìœ í˜•ì„ realmbtië¡œ ë†“ê³ 
  const realMbti = mbtiRanking[0].id;
  const realMbtiShort = mbtiRanking[0].id.slice(0, 4);
  const realMbtiCount = mbtiRanking[0].count;

  let checkType = (myName, myMbti, realMbti, myMbtiCount, realMbtiCount) => {
    if (myMbtiCount - 5 > realMbtiCount) {
      return (
        <div>
          <span className="first-line">
            {myMbti}ê°€ {myMbtiCount}ê°œë¡œ ì••ë„ì ìœ¼ë¡œ ë§ì•˜ì–´ìš”!
          </span>
          <br />
          <span className="second-line">
            {myName}ë‹˜ì€ ì•êµ¬ë¥´ê¸° ë’·êµ¬ë¥´ê¸°í•˜ë©´ì„œ ë´ë„ ì—¬ì§€ ì—†ëŠ” {myMbti}!
          </span>
        </div>
      );
    } else if (myMbti != realMbti && myMbtiCount == realMbtiCount) {
      return (
        <div>
          <span className="first-line">
            {myMbti}ì™€ {realMbtiShort} ë¹™ê³ íŒì—ì„œ ëª¨ë‘ ë™ì¼í•˜ê²Œ {myMbtiCount}ê°œ
            ì„ íƒí–ˆì–´ìš”.
          </span>
          <br />
          <span className="second-line">
            ì•Œê³  ë³´ë‹ˆ {realMbtiShort}ì¼ ìˆ˜ë„...?
          </span>
        </div>
      );
    } else if (myMbtiCount < realMbtiCount) {
      // return `ê° ë¹™ê³ íŒì—ì„œ ${myMbti}ëŠ” ${myMbtiCount}ê°œ, ${realMbtiShort}ëŠ” ${realMbtiCount}ê°œ ì„ íƒí–ˆì–´ìš”. ì•Œê³  ë³´ë©´ ${myName}ë‹˜ì€ ${myMbti}ë³´ë‹¤ ${realMbtiShort}ì˜ ì„±í–¥ì— ë” ê°€ê¹ë‹¤ëŠ”
      // ê²ƒ!`;
      return (
        <div>
          <span className="first-line">
            ê° ë¹™ê³ íŒì—ì„œ {myMbti}ëŠ” {myMbtiCount}ê°œ,
            <br />
            {realMbtiShort}ëŠ” {realMbtiCount}ê°œ ì„ íƒí–ˆì–´ìš”.
          </span>
          <br />
          <span className="second-line">
            ì•Œê³  ë³´ë©´ {myName}ë‹˜ì€
            <br />
            {myMbti}ë³´ë‹¤ {realMbtiShort}ì˜ ì„±í–¥ì— ë” ê°€ê¹ë‹¤ëŠ” ê²ƒ!
          </span>
        </div>
      );
    } else {
      return `${myMbtiCount}ê°œë¡œ ${myMbti} ë¹™ê³ íŒì˜ ì„ íƒ ê°œìˆ˜ê°€ ê°€ì¥ ë§ì•˜ì–´ìš”. ${myName}ë‹˜ì€ ì›ë˜ ì˜ˆìƒëŒ€ë¡œ ${myMbti}ì¼ í™•ë¥ ì´ ë†’ë„¤ìš”`;
      // return (
      //   <div>
      //     <span className="first-line">
      //     {myMbtiCount}ê°œë¡œ {myMbti} ë¹™ê³ íŒì˜ ì„ íƒ ê°œìˆ˜ê°€ ê°€ì¥ ë§ì•˜ì–´ìš”.
      //     </span>
      //     <br />
      //     <span className="second-line">
      //     {myName}ë‹˜ì€ ì›ë˜ ì˜ˆìƒëŒ€ë¡œ {myMbti}ì¼ í™•ë¥ ì´ ë†’ë„¤ìš”
      //     </span>
      //   </div>
      // );
    }
  };

  let renderMistypeRevealItem = (item, i) => {
    return (
      <div className="mistype-container">
        <div className="mistype-title">
          MYSTERY BINGO #{i + 1}
          <span className="mbti accent highlight">
            {mistypeBoardArray[i].mbti}
          </span>
        </div>
        <div className="mistype-bingo">
          <BingoBoard
            mbti={mistypeBoardArray[i].mbti.slice(0, 4)}
            board={mistypeBoardArray[i].board}
            show={true}
            disableClick={true}
          />
        </div>
        <div className="mistype-reason">
          <Accordion
