import "./BingoBoard.css";
import { useLocation } from "react-router-dom";

import bingoContentData from "../bingoContentData.json";
// import mistypeContentData from "../mistypesData.json";

// function BingoBoard({ items }) {
function BingoBoard({ mbti, clickedArray, setClickedArray }) {
  const items = bingoContentData.find((obj) => obj.id == mbti).items;

  // splitting an array into chunks of 5 items each
  let rows = [];
  const size = 5;
  for (let i = 0; i < items.length; i += size)
    rows.push(items.slice(i, i + size));
  // console.log(rows);

  // let rowDivs = [];
  // for (let i = 0; i < rows.length; i++) {
  //   let currentRow = rows[i];
  //   let colDivs = [];
  //   for (let j = 0; j < currentRow.length; j++) {
  //     let currentItem = currentRow[j];
  //     colDivs.push(<div className="col">{currentItem}</div>);
  //   }

  //   rowDivs.push(<div className="row">{colDivs}</div>);
  // }

  let onClickSquare = (rowNum, colNum) => {
    let newClickedArray = [...clickedArray];
    newClickedArray[rowNum][colNum] = !newClickedArray[rowNum][colNum];
    setClickedArray(newClickedArray);
  };

  let hasLongWord = (text) => {
    return text.split(" ").filter((word) => word.length > 5).length > 0;
  };

  // 이거 너무 inefficient한가? specify row/colnum?
  let isFreeSpace = (text) => {
    return text == mbti;
  };

  let isMysteryBingo = (props) => {
    const location = useLocation();
    return location.pathname;
  };

  let renderSquare = (item, rowNum, colNum) => (
    <div
      className={`square ${hasLongWord(item) ? "long" : ""} ${
        clickedArray[rowNum][colNum] ? "activated" : ""
      } ${isFreeSpace(item) ? "unclickable" : ""}`}
      key={`square${colNum}`}
      onClick={() => onClickSquare(rowNum, colNum)}
    >
      {item}
    </div>
  );

  let renderRow = (row, rowNum) => (
    <div className="row" key={`row${rowNum}`}>
      {row.map((item, colNum) => renderSquare(item, rowNum, colNum))}
    </div>
  );

  return (
    <>
      {/* <div className="container">{rowDivs}</div> */}
      <div className="frame">
        <div className="frame-title">BINGO</div>
        <div className="board-container">{rows.map(renderRow)}</div>
      </div>
    </>
  );
}

export default BingoBoard;
