import BingoBoard from "../components/BingoBoard";
import "./ResultsPage.css";
import Accordion from "../components/Accordion";
import RankingBoard from "../components/RankingBoard";

// import mbtiimg from `../images/${realMbti}.png`;

// const myMbtiCount = localStorage.getItem("myMbtiCount")

function ResultsPage() {
  const myName = localStorage.getItem("myName");
  const myMbti = localStorage.getItem("myMbti");
  const myMbtiBoard = JSON.parse(localStorage.getItem("myMbtiBoard"));
  const myMistypes = JSON.parse(localStorage.getItem("myMistypes"));
  const mistypeBoardArray = JSON.parse(
    localStorage.getItem("mistypeBoardArray")
  );

  // let testByRankArray = [
  //   { mbti: "INFP", count: "18" },
  //   { mbti: "ISFJ", count: "16" },
  //   { mbti: "ENFP-Fi", count: "12" },
  //   { mbti: "ISFP", count: "11" },
  // ];

  // Ïù¥Í±∞ ÎåÄÏã† mistypeboardarray ÏïàÏóê ÏûàÎäî id ÏÇ¨Ïö©ÌïòÎäî Í≤å ÎÇ´ÎÇò?
  let myMistypesIds = myMistypes.map((item) => item.id);

  // // Ï§ÄÏö∞ test data (ÏõêÎûò ÏòàÏÉÅÎåÄÎ°úÏù∏ ÏºÄÏù¥Ïä§)
  // const testMistypeCountArray = [12, 11, 16];

  // // ÎÇ¥ test data (ÏïåÍ≥†Î≥¥Îãà Îã§Î•∏ ÏºÄÏù¥Ïä§)
  // const myMbtiCount = 15;
  // const testMistypeCountArray = [17, 8, 9];

  // test data (ÎëêÍ∞ÄÏßÄ ÏÑ†ÌÉùÍ∞úÏàòÍ∞Ä ÎòëÍ∞ôÏùÄ ÏºÄÏù¥Ïä§)

  let trueCount = (board) => {
    let count = 0;
    for (let i = 0; i < board.length; i++) {
      count += board[i].filter(Boolean).length;
    }
    return count;
  };

  // // trueCount mapÏúºÎ°ú Ïì∞Îäî Î≤ï
  // console.log(
  //   myMbtiBoard
  //     .map((row) => row.filter(Boolean).length)
  //     .reduce((a, b) => a + b, 0)
  // );

  let myMbtiCount = trueCount(myMbtiBoard);

  let mistypeCount = mistypeBoardArray.map((item) => {
    return {
      id: item.mbti,
      count: trueCount(item.board),
      reason: myMistypes.map((item) => item.reason),
    };
  });

  let mbtiRanking = [
    ...mistypeCount,
    {
      id: myMbti,
      count: trueCount(myMbtiBoard),
    },
  ].sort((a, b) => {
    return b.count - a.count;
  });

  // 1Îì±Ìïú Ïú†ÌòïÏùÑ realmbtiÎ°ú ÎÜìÍ≥†
  const realMbti = mbtiRanking[0].id;
  const realMbtiCount = mbtiRanking[0].count;

  // when mymbti != realmbti ===>

  // when

  // let addMistypeCount = (mistypeBoardArray) => {
  //   let newMistypeBoardArray = [...mistypeBoardArray];
  //   newMistypeBoardArray.map((board) => {
  //     board.push(trueCount(board));
  //   });
  //   return newMistypeBoardArray;
  // };

  // console.log(addMistypeCount);

  // let addCount = (array) => {
  //   let newArray = [...array];
  //   newArray.map((item) => {item.push([item.count]: trueCount(item.board))
  //   })
  //   return newArray
  // }

  let renderMistypeRevealItem = (item, i) => {
    return (
      <div className="mistype-container">
        <div className="mistype-title">
          MYSTERY BINGO #{i + 1}
          <span className="mbti accent highlight">
            {mistypeBoardArray[i].mbti}
          </span>
        </div>
        <div className="mistype-bingo">
          <BingoBoard
            mbti={mistypeBoardArray[i].mbti.slice(0, 4)}
            board={mistypeBoardArray[i].board}
            show={true}
            disableClick={true}
          />
        </div>
        <div className="mistype-reason">
          <Accordion
            myMbti={myMbti}
            mistype={mistypeBoardArray[i].mbti}
            // reason={myMistypes[myMistypes.indexOf({ mistypeBoardArray[i].mbti })].reason}
            myMistypes={myMistypes}
          />
        </div>
      </div>
    );
  };

  return (
    <>
      {/* conditional rendering. if myMbti !== realMbti */}

      <div className="result">
        <div className="headline">
          <span className="name">{myName}</span>ÎãòÏùò{" "}
          <span className="accent highlight">ÏßÑÏßú MBTI</span>Îäî<br></br>
          <span className="myrealmbti accent highlight">{realMbti}</span> ÏûÖÎãàÎã§
        </div>
        <img
          src={require(`../images/${realMbti.toLowerCase()}.png`)}
          width="300px"
        />

        <div className="result-summary">
          ÏïåÍ≥† Î≥¥Î©¥ {myName} ÎãòÏùÄ {myMbti}Î≥¥Îã§ {realMbti}Ïùò ÏÑ±Ìñ•Ïóê Îçî Í∞ÄÍπùÎã§Îäî
          Í≤É!
        </div>

        <div className="ranking">
          <RankingBoard ranking={mbtiRanking} />
        </div>
      </div>

      <div className="mystery-reveal-title">
        <p>üîÆ ÎØ∏Ïä§ÌÖåÎ¶¨ ÎπôÍ≥†Ïùò Ï†ïÏ≤¥Î•º Í≥µÍ∞úÌï©ÎãàÎã§!</p>
        <div>
          {myMbti}Îì§ÏùÄ Ï¢ÖÏ¢Ö{" "}
          <span className="mistype-names">{myMistypesIds.join(", ")}</span>Îì§Í≥º
          ÌòºÎèôÎêòÍ∏∞ÎèÑ Ìï¥Ïöî.
        </div>
      </div>

      {mistypeBoardArray.map(renderMistypeRevealItem)}

      {/* <div className="mistype-container">
        <div className="mistype-title">
          MYSTERY BINGO <span className="number">#1</span>{" "}
          <span className="mbti accent highlight">ENFP</span>
        </div>
        <div className="mistype-bingo">
          <BingoBoard
            mbti={mistypeBoardArray[0].mbti.slice(0, 4)}
            board={mistypeBoardArray[0].board}
            show={true}
            disableClick={true}
          />
        </div>
        <div className="mistype-reason">
          <Accordion myMbti={myMbti} />
        </div>
      </div> */}
    </>
  );
}

export default ResultsPage;

