import "./ResultsPage.css";
// import mbtiimg from "../images/infp.png";
// import mbtiimg from `../images/${realMbti}.png`;

function ResultsPage() {
  const myName = localStorage.getItem("myName");
  const myMbti = localStorage.getItem("myMbti");
  const realMbti = "ENFP";
  const myMbtiCount = 10;
  const realMbtiCount = 20;
  const myMistypes = JSON.parse(localStorage.getItem("myMistypes"));

  let myMistypesIds = myMistypes.map((item) => item.id);
  console.log(myMistypesIds);

  return (
    <>
      <h1>결과</h1>

      {/* conditional rendering. if myMbti !== realMbti */}

      <div className="realmbti-reveal">
        <p>
          {myName}님의 진짜 MBTI는 {realMbti}입니다
        </p>
        {/* <img src={`../images/${realMbti}.png`} width="100%" /> */}
        {/* <img src={`../images/infp.png`} width="100%" /> */}
        {/* <img src={mbtiimg} width="300px" /> */}
        {/* public folder에 이미지 폴더 넣으면 따로 import 안 해도 불러올 수 있음 */}
        <img src={`images/${realMbti}.png`} width="80%" />
        {/* <td>
          <div className="img-container">
            <img className="top" src={`images/${realMbti}.png`} width="80%" />
            <img className="bottom" src={"images/circle.png"} width="80%" />
          </div>
        </td> */}
      </div>

      <div className="result">
        <div className="result-count">
          각 빙고판에서 {myMbti}는 {myMbtiCount}개, <br />
          {realMbti}는 {realMbtiCount}개 선택했어요. <br />
        </div>
        <div className="result-summary">
          알고 보면 {myName} 님은 사실 {myMbti}보다 {realMbti}의 성향에 더
          가깝다는 것!
        </div>
      </div>

      <div className="mystery-reveal">
        <p>미스테리 빙고의 정체를 공개합니다!</p>
      </div>

      <div className="mistype-reason">
        <p>
          {/* {myMbti}들은 종종 {mistypesINFP.join(", ")}들과 혼동되기도 해요. */}
          {/* {myMbti}들은 종종 {myMistypes.id.join(", ")}들과 혼동되기도 해요. */}
          {myMbti}들은 종종 {myMistypesIds.join(", ")}들과 혼동되기도 해요.
        </p>
        {/* <div className="mistype-list"> */}
        <div>
          <div className="mistype">
            {/* {myMistypes[0]} <br /> */}
            펼쳐서 설명 보기
          </div>
          <div className="mistype">
            {/* {myMistypes[1]} <br /> */}
            펼쳐서 설명 보기
          </div>
        </div>
      </div>
    </>
  );
}

export default ResultsPage;
