          <input
            id={leftoption}
            className="radio-btn"
            type="radio"
            value={leftoption}
            name={criteria}
          />
          <label htmlFor={leftoption}>{leftoption}</label>
        </div>
        {/* <span className="criteria-name">
          {leftoptionlabel}
          <span className="criteria-name-container">
            ⬅<span className="criteria-name-label">{criterialabel}</span>➡
          </span>
