  // test data (두가지 선택개수가 똑같은 케이스)

  // 이거 대신 mistypeboardarray 안에 있는 id 사용하는 게 낫나?
  let myMistypesIds = myMistypes.map((item) => item.id);

  let trueCount = (board) => {
    let count = 0;
    for (let i = 0; i < board.length; i++) {
      count += board[i].filter(Boolean).length;
    }
    return count;
  };

  // // trueCount map으로 쓰는 법
  // console.log(
  //   myMbtiBoard
  //     .map((row) => row.filter(Boolean).length)
  //     .reduce((a, b) => a + b, 0)
  // );

  let myMbtiCount = trueCount(myMbtiBoard);

  let mistypeCount = mistypeBoardArray.map((item, i) => {
    return {
      id: item.mbti,
      count: trueCount(item.board),
    };
  });

  // let mistypeReason = myMistypes.map((item) => item.reason);
  // console.log(mistypeReason);

  let sortByCount = [
    ...mistypeCount,
    {
      id: myMbti,
      count: trueCount(myMbtiBoard),
    },
  ].sort((a, b) => {
    return b.count - a.count;
  });

  // 각 object에 ranking이라는 property attribute 추가하기
  let addRanking = sortByCount.map((obj, i) => {
    return { ranking: i + 1, ...obj };
  });

  // 앞 object와 value 비교해서 같으면 같은 등수로 바꿔주기
  for (let i = 1; i < typeImplementation.length; i++) {
    if (addRanking[i].count == addRanking[i - 1].count) {
      addRanking[i].ranking = addRanking[i - 1].ranking;
    }
  }

  // 1등한 유형을 realmbti로 놓기
  // const realMbti = sortByCount[0].id;
  // const realMbtiShort = sortByCount[0].id.slice(0, 4);
  // const realMbtiCount = sortByCount[0].count;

  let firstPlaceArray = addRanking.filter((obj) => obj.ranking === 1);

  let realMbti = firstPlaceArray.map((obj) => obj.id).join(" & ");
  let realMbtiShort = firstPlaceArray
    .map((obj) => obj.id)
    .slice(0, 4)
    .join(" & ");
  // or로 바꿀까?

  let realMbtiCount = firstPlaceArray[0].count;
  // 얘넨 왜 안 됐을까?
