  let checkType = (myMbti, realMbti, myMbtiCount, realMbtiCount) => {
    if (myMbtiCount - 5 > realMbtiCount) {
      return setType("definite");
    } else if (myMbti != realMbti && myMbtiCount == realMbtiCount) {
      return setType("tie");
    } else if (myMbtiCount < realMbtiCount) {
      return setType("surprise");
    }
  };

  console.log(type);

  // let type = "likely";

  // let checkType = (myMbtiCount, realMbtiCount) => {
  //   if (myMbtiCount - 5 > realMbtiCount) {
