import "./MbtiCriteria.css";

function MbtiCriteria({
  criteria,
  criterialabel,
  leftoption,
  leftoptionlabel,
  rightoption,
  rightoptionlabel,
  // setMyMbti,
}) {
  // const [myMbti, setMyMbti] = useState(["", "", "", ""]);

  let criteriaNames = [
    "energy",
    "info-processing",
    "info-evaluation",
    "lifestyle",
  ];

  // let myMbti = [];

  // document.querySelector("button").addEventListener("click", function (event) {
  //   myMbti = criteriaNames.map(function (el) {
  //     return document.querySelector(
  //       "input[criteriaNames='" + el + "']:checked"
  //     ).value;
  //   });

  //   console.log(myMbti);
  // });

  return (
    <>
      <div className="criteria">
        <input
          id={leftoption}
          className="radio-btn"
          type="radio"
          value={leftoption}
          name={criteria}
        />
        <label htmlFor={leftoption}>
          <span>{leftoption}</span>
        </label>
        <span className="criteria-name">
          {leftoptionlabel}
          <span className="criteria-name-container">
            ⬅<span className="criteria-name-label">{criterialabel}</span>➡
          </span>
          {rightoptionlabel}
        </span>
        <input
          id={rightoption}
          className="radio-btn"
          type="radio"
          value={rightoption}
          name={criteria}
        />
        <label htmlFor={rightoption}>{rightoption}</label>
      </div>
    </>
  );
}

export default MbtiCriteria;
