  //     colDivs.push(<div className="col">{currentItem}</div>);
  //   }

  //   rowDivs.push(<div className="row">{colDivs}</div>);
  // }

  let renderCol = (item) => <div className="col">{item}</div>;

  let renderRow = (row) => <div className="row">{row.map(renderCol)}</div>;

  return (
    <>
      {/* <div className="container">{rowDivs}</div> */}
      <div className="container">{rows.map(renderRow)}</div>

      {/* <div>
        {items.map((item) => (
          <li>{item}</li>
        ))}
      </div> */}

      {/* <div>
        {rows.map((row) => (
          <div>{row}</div>
        ))}
      </div> */}
    </>
  );
}

export default BingoBoard;
