import BingoBoard from "../components/BingoBoard";
import "./ResultsPage.css";
import Accordion from "../components/Accordion";
import RankingBoard from "../components/RankingBoard";

import downarrow from "../images/downarrow.png";

// import definite from "../images/.png"
// import likely from "../images/.png"
// import tie from "../images/.png"
import surprise from "../images/surprise.gif";
import { typeImplementation } from "@testing-library/user-event/dist/type/typeImplementation";

// const myMbtiCount = localStorage.getItem("myMbtiCount")

function ResultsPage() {
  const myName = localStorage.getItem("myName");
  const myMbti = localStorage.getItem("myMbti");
  const myMbtiBoard = JSON.parse(localStorage.getItem("myMbtiBoard"));
  const myMistypes = JSON.parse(localStorage.getItem("myMistypes"));
  const mistypeBoardArray = JSON.parse(
    localStorage.getItem("mistypeBoardArray")
  );

  // let testByRankArray = [
  //   { mbti: "INFP", count: "18" },
  //   { mbti: "ISFJ", count: "16" },
  //   { mbti: "ENFP-Fi", count: "12" },
  //   { mbti: "ISFP", count: "11" },
  // ];

  // // ì¤€ìš° test data (ì›ë˜ ì˜ˆìƒëŒ€ë¡œì¸ ì¼€ì´ìŠ¤)
  // const testMistypeCountArray = [12, 11, 16];

  // // ë‚´ test data (ì•Œê³ ë³´ë‹ˆ ë‹¤ë¥¸ ì¼€ì´ìŠ¤)
  // const myMbtiCount = 15;
  // const testMistypeCountArray = [17, 8, 9];

  // test data (ë‘ê°€ì§€ ì„ íƒê°œìˆ˜ê°€ ë˜‘ê°™ì€ ì¼€ì´ìŠ¤)

  // ì´ê±° ëŒ€ì‹  mistypeboardarray ì•ˆì— ìˆëŠ” id ì‚¬ìš©í•˜ëŠ” ê²Œ ë‚«ë‚˜?
  let myMistypesIds = myMistypes.map((item) => item.id);

  let trueCount = (board) => {
    let count = 0;
    for (let i = 0; i < board.length; i++) {
      count += board[i].filter(Boolean).length;
    }
    return count;
  };

  // // trueCount mapìœ¼ë¡œ ì“°ëŠ” ë²•
  // console.log(
  //   myMbtiBoard
  //     .map((row) => row.filter(Boolean).length)
  //     .reduce((a, b) => a + b, 0)
  // );

  let myMbtiCount = trueCount(myMbtiBoard);

  let mistypeCount = mistypeBoardArray.map((item, i) => {
    return {
      id: item.mbti,
      count: trueCount(item.board),
    };
  });

  // let mistypeReason = myMistypes.map((item) => item.reason);
  // console.log(mistypeReason);

  let sortByCount = [
    ...mistypeCount,
    {
      id: myMbti,
      count: trueCount(myMbtiBoard),
    },
  ].sort((a, b) => {
    return b.count - a.count;
  });

  // ê° objectì— rankingì´ë¼ëŠ” property attribute ì¶”ê°€í•˜ê¸°
  let addRanking = sortByCount.map((obj, i) => {
    return { ranking: i + 1, ...obj };
  });

  // ì• objectì™€ value ë¹„êµí•´ì„œ ê°™ìœ¼ë©´ ê°™ì€ ë“±ìˆ˜ë¡œ ë°”ê¿”ì£¼ê¸°
  for (let i = 1; i < typeImplementation.length; i++) {
    if (addRanking[i].count == addRanking[i - 1].count) {
      addRanking[i].ranking = addRanking[i - 1].ranking;
    }
  }

