import BingoBoard from "../components/BingoBoard";
import "./ResultsPage.css";
// import mbtiimg from "../images/infp.png";
// import mbtiimg from `../images/${realMbti}.png`;

// const myMbtiCount = localStorage.getItem("myMbtiCount")

function ResultsPage() {
  const myName = localStorage.getItem("myName");
  const myMbti = localStorage.getItem("myMbti");
  const realMbti = "ENFP";
  const realMbtiCount = 20;
  const myMbtiBoard = JSON.parse(localStorage.getItem("myMbtiBoard"));
  const myMistypes = JSON.parse(localStorage.getItem("myMistypes"));
  const mistypeBoardArray = JSON.parse(
    localStorage.getItem("mistypeBoardArray")
  );

  let myMistypesIds = myMistypes.map((item) => item.id);

  // 왜 결과값이 아니라 코드가 그대로 출력되지?
  // let myMbtiCount = (array) => {
  //   let count = 0;
  //   for (let i = 0; i < myMbtiBoard.length; i++) {
  //     count += array[i].filter(Boolean).length;
  //   }
  //   return count;
  // };

  // THIS!!!!
  let trueCount = (board) => {
    let count = 0;
    for (let i = 0; i < board.length; i++) {
      count += board[i].filter(Boolean).length;
    }
    return count;
  };

  // trueCount map으로 쓰는 법?

  // let trueCount = (board) => {
  //   counter = 0;
  //   board.map((array, counter) => {
  //     return (counter += array.filter(Boolean).length);
  //   });
  // };

  // let sum = (r, a) => r.map(b, i) => a[i] + b;

  // let trueCount = (board) => {
  //   return board.map((array) => {
  //     return array.filter(Boolean).length;
  //   });
  // };

  // let trueCount = (board) => {
  //   return (
  //     (sum) =>
  //       sum.reduce( ()
  //         board.map((array) => {
  //           return array.filter(Boolean).length;
  //         })
  //       ),
  //     0
  //   );
  // };

  let myMbtiCount = trueCount(myMbtiBoard);
  console.log(myMbtiCount);

  // let mistypeCount = trueCount(mistypeBoardArray[2].board);
  // console.log(mistypeBoardArray[0]);
  // console.log(mistypeCount);

  // let mistypeCountArray = [];

  // let mistypeCount = mistypeBoardArray.map((board) => {id: board.mbti, count: trueCount(board)})

  // let mistypeCount = mistypeBoardArray.map((board) => ({
  //   [board.mbti]: trueCount(board),
  // }));
  // console.log(mistypeCount);

  // let mistypeCount = mistypeBoardArray.map((board) => ({
  //   [board.mbti]: trueCount(board),
  // }));
  // console.log(mistypeCount);

  return (
    <>
      <h1>결과</h1>

      {/* conditional rendering. if myMbti !== realMbti */}

      <div className="realmbti-reveal">
        <p>
          {myName}님의 진짜 MBTI는 {realMbti}입니다
        </p>
        {/* <img src={`../images/${realMbti}.png`} width="100%" /> */}
        {/* <img src={`../images/infp.png`} width="100%" /> */}
        {/* <img src={mbtiimg} width="300px" /> */}
        {/* public folder에 이미지 폴더 넣으면 따로 import 안 해도 불러올 수 있음 */}
        <img src={`images/${realMbti}.png`} width="80%" />
        {/* <td>
          <div className="img-container">
            <img className="top" src={`images/${realMbti}.png`} width="80%" />
            <img className="bottom" src={"images/circle.png"} width="80%" />
          </div>
        </td> */}
      </div>

      <div className="result">
        <div className="result-count">
          각 빙고판에서 {myMbti}는 {myMbtiCount}개, <br />
          {realMbti}는 {realMbtiCount}개 선택했어요. <br />
        </div>
        <div className="result-summary">
          알고 보면 {myName} 님은 사실 {myMbti}보다 {realMbti}의 성향에 더
          가깝다는 것!
        </div>
      </div>

      <div className="mystery-reveal">
        <p>미스테리 빙고의 정체를 공개합니다!</p>
      </div>

      <div className="mistype-reason">
        <p>
          {/* {myMbti}들은 종종 {mistypesINFP.join(", ")}들과 혼동되기도 해요. */}
          {/* {myMbti}들은 종종 {myMistypes.id.join(", ")}들과 혼동되기도 해요. */}
          {myMbti}들은 종종 {myMistypesIds.join(", ")}들과 혼동되기도 해요.
        </p>
        {/* <div className="mistype-list"> */}
        <div>
          <div className="mistype">
            {/* {myMistypes[0]} <br /> */}
            {/* <BingoBoard /> */}
            펼쳐서 설명 보기
          </div>
          <div className="mistype">
            {/* {myMistypes[1]} <br /> */}
            펼쳐서 설명 보기
          </div>
        </div>
      </div>
    </>
  );
}

export default ResultsPage;
