import BingoBoard from "../components/BingoBoard";
import "./ResultsPage.css";
import Accordion from "../components/Accordion";
import RankingBoard from "../components/RankingBoard";

import downarrow from "../images/downarrow.png";

import definite from "../images/definite.gif";
import likely from "../images/likely.gif";
import tie from "../images/tie.gif";
import surprise from "../images/surprise.gif";

import { typeImplementation } from "@testing-library/user-event/dist/type/typeImplementation";
import React, { useEffect, useState } from "react";

// import Typical from "react-typical";
import { Typewriter } from "react-simple-typewriter";

// const myMbtiCount = localStorage.getItem("myMbtiCount")

function ResultsPage() {
  const myName = localStorage.getItem("myName");
  const myMbti = localStorage.getItem("myMbti");
  const myMbtiBoard = JSON.parse(localStorage.getItem("myMbtiBoard"));
  const myMistypes = JSON.parse(localStorage.getItem("myMistypes"));
  const mistypeBoardArray = JSON.parse(
    localStorage.getItem("mistypeBoardArray")
  );

  // let testByRankArray = [
  //   { mbti: "INFP", count: "18" },
  //   { mbti: "ISFJ", count: "16" },
  //   { mbti: "ENFP-Fi", count: "12" },
  //   { mbti: "ISFP", count: "11" },
  // ];

  // // 준우 test data (원래 예상대로인 케이스)
  // const testMistypeCountArray = [12, 11, 16];

  // // 내 test data (알고보니 다른 케이스)
  // const myMbtiCount = 15;
  // const testMistypeCountArray = [17, 8, 9];

  // test data (두가지 선택개수가 똑같은 케이스)

  // 이거 대신 mistypeboardarray 안에 있는 id 사용하는 게 낫나?
  let myMistypesIds = myMistypes.map((item) => item.id);

  let trueCount = (board) => {
    let count = 0;
    for (let i = 0; i < board.length; i++) {
      count += board[i].filter(Boolean).length;
    }
    return count;
  };

  // // trueCount map으로 쓰는 법
  // console.log(
  //   myMbtiBoard
  //     .map((row) => row.filter(Boolean).length)
  //     .reduce((a, b) => a + b, 0)
  // );

  let myMbtiCount = trueCount(myMbtiBoard);

  let mistypeCount = mistypeBoardArray.map((item, i) => {
    return {
      id: item.mbti,
      count: trueCount(item.board),
    };
  });

  // let mistypeReason = myMistypes.map((item) => item.reason);
  // console.log(mistypeReason);

  let sortByCount = [
    ...mistypeCount,
    {
      id: myMbti,
      count: trueCount(myMbtiBoard),
    },
  ].sort((a, b) => {
    return b.count - a.count;
  });

  // 각 object에 ranking이라는 property attribute 추가하기
  let addRanking = sortByCount.map((obj, i) => {
    return { ranking: i + 1, ...obj };
  });

  // 앞 object와 value 비교해서 같으면 같은 등수로 바꿔주기
  for (let i = 1; i < typeImplementation.length; i++) {
    if (addRanking[i].count == addRanking[i - 1].count) {
      addRanking[i].ranking = addRanking[i - 1].ranking;
    }
  }

  // 1등한 유형을 realmbti로 놓기
  // const realMbti = sortByCount[0].id;
  // const realMbtiShort = sortByCount[0].id.slice(0, 4);
  // const realMbtiCount = sortByCount[0].count;

  let firstPlaceArray = addRanking.filter((obj) => obj.ranking === 1);

  let realMbti = firstPlaceArray.map((obj) => obj.id).join(" & ");
  let realMbtiShort = firstPlaceArray
    .map((obj) => obj.id)
    .slice(0, 4)
    .join(" & ");
  // or로 바꿀까?

  let realMbtiCount = firstPlaceArray[0].count;
  // 얘넨 왜 안 됐을까?
  // let realMbtiCount = firstPlaceArray.map((obj) => obj.count).join(""));
  // let realMbtiCount = firstPlaceArray.map((obj) => obj.count).toString();
  // console.log(realMbtiCount.toString());

  // THIS WORKS BUT ERROR: TOO MANY RERENDERS
  // let [type, setType] = useState("");
  // if (myMbtiCount - 5 > realMbtiCount) {
  //   setType("definite");
  // } else if (myMbti != realMbti && myMbtiCount == realMbtiCount) {
  //   setType("tie");
  // } else if (myMbtiCount < realMbtiCount) {
  //   setType("surprise");
  // } else if (myMbtiCount > realMbtiCount) {
  //   setType("likely");
  // }

  let type = "";
  if (myMbtiCount - 5 > realMbtiCount) {
    type = "definite";
  } else if (myMbti != realMbti && myMbtiCount == realMbtiCount) {
    type = "tie";
  } else if (myMbtiCount < realMbtiCount) {
    type = "surprise";
  } else if (myMbtiCount > realMbtiCount) {
    type = "likely";
  }

  // type = "tie";

  let renderResultSummary = (
    myMbti,
    realMbtiShort,
    myMbtiCount,
    realMbtiCount,
    type
  ) => {
    // definite
    if (type === "definite") {
      return (
        <div className="first-line">
          <span className="accent color">{myMbti}</span>가{" "}
          <span className="accent color">{myMbtiCount}개</span>로 압도적으로
          많았어요!
        </div>
      );
    }
    // tie
    else if (type === "tie") {
      return (
        <div className="first-line">
          <span className="accent color">{myMbti}</span>와{" "}
          <span className="accent color">{realMbtiShort}</span> 빙고판에서 모두
          동일하게 <span className="accent color">{myMbtiCount}개</span>{" "}
          선택했어요.
        </div>
      );
    }
    // surprise
    else if (type === "surprise") {
      return (
        <div className="first-line">
          원래 MBTI인 줄 알았던 <span className="accent color">{myMbti}</span>의
          빙고에서는 <span className="accent color">{myMbtiCount}개</span>
          를 선택한 반면,
          <br />
          <span className="accent color">{realMbtiShort}</span> 빙고에서는
          그보다 더 많은 <span className="accent color">{realMbtiCount}개</span>
          를 선택했어요.
        </div>
      );
    }
    // likely
    else if (type === "likely") {
      return (
        <div className="first-line">
          <span className="accent color">{myMbtiCount}개</span>로{" "}
          <span className="accent color">{myMbti}</span> 빙고판의 선택 개수가
          가장 많았어요.
        </div>
      );
    }
  };

  let renderReaction = (myMbti, realMbtiShort, type) => {
    // definite
    if (type === "definite") {
      return (
        <div className="reaction">
          <img src={definite} loop="infinite" />
          <div className="reaction-text accent highlight">
            앞구르기 뒷구르기하면서 봐도 {myMbti}
          </div>
        </div>
      );
    }
    // tie
    else if (type === "tie") {
      return (
        <div className="reaction">
          <img src={tie} />
          <div className="reaction-text accent highlight">
            내 안에 싸우고 있는 {firstPlaceArray.length}개의 자아
            <br /> 나...어쩌면 {realMbtiShort}일지도...?
          </div>
        </div>
      );
    }
    // surprise
    else if (type === "surprise") {
      return (
        <div className="reaction">
          <img src={surprise} />
          <div className="reaction-text accent highlight">
            한평생 {myMbti}로 살아온 내가 사실은 {realMbtiShort}...?!
          </div>
        </div>
      );
    }
    // likely
    else if (type === "likely") {
      return (
        <div className="reaction">
          <img src={likely} />
          <div className="reaction-text accent highlight">
            MBTI는 과학이다— 아무래도 {myMbti} 맞는 듯~!
          </div>
        </div>
      );
    }
  };

  function originalBingoTitle(type) {
    switch (type) {
      case "surprise":
        return "나의 MBTI(였던 것)";
        break;
      case "tie":
        return "나의 MBTI(일 수도~ 아닐 수도~)";
        break;
      default:
        return "나의 MBTI 빙고";
    }
  }

  let renderMistypeRevealItem = (item, i) => {
    return (
      <div className="mistype-container">
        {/* <div className="mistype-title">MYSTERY BINGO #{i + 1}</div>
        <div className="mbti accent highlight">{mistypeBoardArray[i].mbti}</div> */}
        <span className="mistype-title">MYSTERY BINGO #{i + 1} </span>
        <span className="mbti accent highlight">
          {mistypeBoardArray[i].mbti}
        </span>

        <div className="mistype-bingo">
          <BingoBoard
            mbti={mistypeBoardArray[i].mbti.slice(0, 4)}
            board={mistypeBoardArray[i].board}
            show={true}
            disableClick={true}
          />
        </div>
        <div className="mistype-reason">
          <Accordion
            myMbti={myMbti}
            mistype={mistypeBoardArray[i].mbti}
            myMistypes={myMistypes}
          />
        </div>
      </div>
    );
  };

  // const Component = () => {
  //   const [showElement, setShowElement] = React.useState(true);
  //   useEffect(() => {
  //     setTimeout(function () {
  //       setShowElement(false);
  //     }, 10000);
  //   }, []);

  //   return (
  //     <div className="transition">
  //       {showElement ? (
  //         <>
