import "./MysteryBingoPage.css";
import { Link, useParams } from "react-router-dom";
import BingoBoard from "../components/BingoBoard";
import { useState } from "react";
import mistypesData from "../mistypesData.json";

function MysteryBingoPage() {
  // const { mysteryMbtiBingo } = useParams();
  // const mysteryBingo = getBingoBySlug(mysteryMbtiBingo);
  const myName = localStorage.getItem("myName");
  const myMbti = localStorage.getItem("myMbti");
  // const myMistypes = localStorage.getItem("myMistypes");
  const myMistypes = JSON.parse(localStorage.getItem("myMistypes"));

  // mistype들의 clickedcount를 push? (results 때문에 비교해야 하니까)
  // let myMistypeCounts = [];

  // let currMbti = myMistypes[0].id.substring(0, 4);
  const [curIndex, setCurIndex] = useState(0);
  let curMbti = myMistypes[curIndex].id.substring(0, 4);

  console.log(curMbti);
  console.log(curIndex);

  // let mistypesLoop = (array) => {
  //   // for (let i = 0; i < myMistypes.length; i++) {
  //   //   let currMbti = myMistypes[i].id.substring(0, 4);
  //   // }
  //   currIndex++;
  //   return currMbti;
  // };

  // let currMbti = myMistypes[2].id.substring(0, 4);
  // console.log(currMbti);

  // let myMistypes.map((mistype) => (<Link to={`/mysteryMbtiBingo/${mistype.id}`}></Link))

  const [boardArray, setBoardArray] = useState(
    new Array(myMistypes.length)
      .fill(false)
      .map((x) => [
        new Array(5).fill(false),
        new Array(5).fill(false),
        new Array(5).fill(false),
        new Array(5).fill(false),
        new Array(5).fill(false),
      ])
  );

  let changeBoardWithIndex = (index) => {
    return (newBoard) => {
      let newBoardArray = [...boardArray];
      newBoardArray[index] = newBoard;
      setBoardArray(newBoardArray);
    };
  };

  // let clickedCount = (array) => {
  //   let count = 0;
  //   for (let i = 0; i < clickedArray.length; i++) {
  //     count += array[i].filter(Boolean).length;
  //   }
  //   return count;
  // };

  // let Conditional = () => {
  //   // onclick하면서 mistype{i}의 i++도 해주고 mistype{i}의 clickedCount도 localstorage해줘야 함
  //   // i++하다가 마지막에 undefined/false가 되면 to="/results" + onclick count storage
  // };

  let renderBoard = (board, i) => {
    return (
      <BingoBoard
        mbti={curMbti}
        board={board}
        onBoardChange={changeBoardWithIndex(i)}
        show={curIndex == i}
      />
    );
  };

  return (
    <>
      <div className="progress">
        {curIndex + 1} / {myMistypes.length}
      </div>
      {/* <div className="bingo-instruction">
        {myName} 님에게 해당되는 설명의 칸을 <br></br> 모두 선택한 후 '완료'
        버튼을 눌러주세요!
      </div> */}

      {boardArray.map(renderBoard)}

      <div>
        {curIndex < myMistypes.length ? (
          <button
            className="btn"
            onClick={(e) => {
              setCurIndex(curIndex + 1);
            }}
          >
            다음
          </button>
        ) : (
          <Link
            to="/results"
            className="btn"
            // onClick={(e) =>
            //   localStorage.setItem(
            //     `mistype${curIndex + 1}Count`,
            //     clickedCount(clickedArray)
            //   )
            // }
          >
            완료
          </Link>
        )}
      </div>

      {/* <div>
        {curIndex < myMistypes.length ? (
          <Link
            to="/mysterybingo"
            className="btn"
            onClick={(e) => {
              localStorage.setItem(
                `mistype${curIndex + 1}Count`,
                clickedCount(clickedArray)
              );
              curIndex++;
            }}
          >
            완료
          </Link>
        ) : (
          <Link
            to="/results"
            className="btn"
            onClick={(e) =>
              localStorage.setItem(
                `mistype${curIndex + 1}Count`,
                clickedCount(clickedArray)
              )
            }
          >
            완료
          </Link>
        )}
      </div> */}
    </>
  );
}

export default MysteryBingoPage;
